# -*- coding: utf-8 -*-
import os
import json
import tccli.options_define as OptionsDefine
import tccli.format_output as FormatOutput
from tccli.nice_command import NiceCommand
import tccli.error_msg as ErrorMsg
import tccli.help_template as HelpTemplate
from tccli import __version__
from tccli.utils import Utils
from tccli.configure import Configure
from tencentcloud.common import credential
from tencentcloud.common.profile.http_profile import HttpProfile
from tencentcloud.common.profile.client_profile import ClientProfile
from tencentcloud.rkp.v20191209 import rkp_client as rkp_client_v20191209
from tencentcloud.rkp.v20191209 import models as models_v20191209
from tccli.services.rkp import v20191209
from tccli.services.rkp.v20191209 import help as v20191209_help


def doGetOpenId(argv, arglist):
    g_param = parse_global_arg(argv)
    if "help" in argv:
        show_help("GetOpenId", g_param[OptionsDefine.Version])
        return

    param = {
        "DeviceToken": argv.get("--DeviceToken"),
        "BusinessId": Utils.try_to_json(argv, "--BusinessId"),
        "BusinessUserId": argv.get("--BusinessUserId"),
        "Platform": Utils.try_to_json(argv, "--Platform"),
        "Option": argv.get("--Option"),

    }
    cred = credential.Credential(g_param[OptionsDefine.SecretId], g_param[OptionsDefine.SecretKey])
    http_profile = HttpProfile(
        reqTimeout=60 if g_param[OptionsDefine.Timeout] is None else int(g_param[OptionsDefine.Timeout]),
        reqMethod="POST",
        endpoint=g_param[OptionsDefine.Endpoint]
    )
    profile = ClientProfile(httpProfile=http_profile, signMethod="HmacSHA256")
    mod = CLIENT_MAP[g_param[OptionsDefine.Version]]
    client = mod.RkpClient(cred, g_param[OptionsDefine.Region], profile)
    client._sdkVersion += ("_CLI_" + __version__)
    models = MODELS_MAP[g_param[OptionsDefine.Version]]
    model = models.GetOpenIdRequest()
    model.from_json_string(json.dumps(param))
    rsp = client.GetOpenId(model)
    result = rsp.to_json_string()
    jsonobj = None
    try:
        jsonobj = json.loads(result)
    except TypeError as e:
        jsonobj = json.loads(result.decode('utf-8')) # python3.3
    FormatOutput.output("action", jsonobj, g_param[OptionsDefine.Output], g_param[OptionsDefine.Filter])


